/**
 * A clickable link containing the website logo.
 * @param href the route to open (default is '/').
 * @param replace Whether to replace history or not (default to false).
 * @param className Class names for styling.
 * @param textSize Text sizing.
 * @param vertical Is company name bellow the logo?
 * @constructor
 */
export default function LogoLink({
                                     href = '/',
                                     replace = false,
                                     className = '',
                                     size = 'lg',
                                     vertical,
                                     light = false,
                                 }: {
    href?: string,
    replace?: boolean,
    className?: string,
    size?: 'sm' | 'md' | 'lg' | 'xl' | '2xl' | '4xl' | '5xl' | 'mdOnMobile',
    vertical?: boolean,
    light?: boolean,
})
{
    return (
        <Link replace={replace} href={href}
              aria-label="Go to home"
              className={`flex items-center justify-center gap-2 outline-primary group ${className} ${vertical && 'flex-col'}`}>
            <Icon light={light}
                  className={`${size === 'sm' && 'h-5'}
                            ${size === 'md' && 'h-6'}
                            ${size === 'lg' && 'h-7'}
                            ${size === 'xl' && 'h-8'}
                            ${size === '2xl' && 'h-10'}
                            ${size === '4xl' && 'h-12'}
                            ${size === '5xl' && 'h-20'}
                            ${size === 'mdOnMobile' && 'size-8 sm:size-9'}
                            `}/>
        </Link>
    );
}

